import axios from 'axios'

const mylink="http://localhost:8082/ZealousEmpDetails"

// mylink/create
// mylink/createpayslip
// mylink/delete/{value}

// async,await

export const loadEmp=async(obj)=>{
    try{
        const txt=obj.username+":"+obj.password
        const token=btoa(txt)
        const t = await axios.get(`${link}/maddy/${obj.username}`,{
            headers:{
                "Authorization":`Basic ${token}`
            }
        })
        if(t.data)
        {
            sessionStorage.setItem("employee",obj.username)
            sessionStorage.setItem("user",token)
            return t
        }
    }
    catch(err){
        alert("invalid")
    }
}

export const join=async(object)=>
{
    const temp=await axios.post(`${mylink}/create`,object)

    alert(JSON.stringify(temp.data)+" has been added successfully ");

    return temp;
}
